import React, { useState } from 'react';
import './registro.css';
import { useNavigate } from 'react-router-dom';

function FormularioUsuario  () {
  const [nombre, setNombre] = useState('');
  const [contraseña, setContraseña] = useState('');
  const [correo, setCorreo] = useState('');
  const [telefono, setTelefono] = useState('');
  const [mensaje, setMensaje] = useState('');

  const manejarEnvio = async (e) => {
    e.preventDefault();

    const nuevoUsuario = {
      nombre: nombre,
      contraseña: contraseña,
      correo: correo,
      telefono: telefono
    };

    try {
      const respuesta = await fetch('/api/usuarios/crear-usuario', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(nuevoUsuario),
        credentials: 'include' 
    });
    

      const data = await respuesta.json();
      
      if (respuesta.ok) {
        setMensaje(data.message);
        setNombre('');
        setContraseña('');
        setCorreo('');
        setTelefono('');
        agregarNombre(data.usuario.nombre);
        fetchClientes();
        fetchTelefono();
        navigate('/ListaClientes');
      } else {
        setMensaje(data.message || 'Error al crear el usuario');
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      setMensaje('Error en la solicitud');
    }
  };

  return (
    <div className='center2'>
      <h1>Crear Usuario</h1>
      <form onSubmit={manejarEnvio}>
        <div className='inputbox'>
        <span>Nombre:</span>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div className='inputbox'>
        <span>Contraseña:</span>
          <input
            type="password"
            value={contraseña}
            onChange={(e) => setContraseña(e.target.value)}
            required
          />
        </div>
        <div className='inputbox'>
          <span>Correo:</span>
          <input
            type="email"
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
            required
          />
        </div>
        <div className='inputbox'>
          <span>Celular:</span>
          <input
            type="text"
            value={telefono}
            onChange={(e) => setTelefono(e.target.value)}
            required
          />
        </div>
        <button id='btn11' className='inputbox' type="submit">Crear Usuario</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
};

export default FormularioUsuario;
