import React, { useEffect, useState } from "react";
import FormularioUsuario from "./componentes/registroUsuarios.jsx";
import FormularioInicio from "./componentes/iniciosesion.jsx";
import FormCliente from "./componentes/crearCliente.jsx";
import EditarCliente from "./componentes/editarCliente.jsx";
import './paneluser.css';
import {
  Table,
  TableBody,
  TableCaption,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
  TableFooter
} from "./componentes/table.jsx";

import jsPDF from 'jspdf';
import Dataimage from './img.js';


function  ClientesXml() {
  const [clientesXml, setClientesXml] = useState([]);
  const [nombre, setNombre] = useState('');
  const [telefono, setTelefono] = useState('');
  const [clienteSelec, setClienteSelec] = useState(null);
  fetch('/api/clientes/lista-completa',{
    method: 'GET',
    credentials: 'include'
  })
  .then(res => res.json())
  .then(data => {
    if(data.listaCompleta){
      setClientesXml(data.listaCompleta);
    }
  })
  .catch(error => {
    console.log('Error al buscar lista de clientes: ',error);
  });
};
  

function  ListaClientes() {
  const [clientes, setClientes] = useState([]);
  const [nombre, setNombre] = useState('');
  const [telefono, setTelefono] = useState('');
  const [clienteSelec, setClienteSelec] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [mostrarFormularioCliente, setMostrarFormularioCliente] = useState(false);
  

  const fetchNombre = () => {
    fetch('/api/usuarios/nombre',{
      method: 'GET',
      credentials: 'include'
    })
      .then(res => res.json())
      .then(data => {
        if (data.nombre) {
          setNombre(data.nombre);
        }
      })
      .catch(error => {
        console.log('Error al obtener el nombre del usuario:', error);
      });
  };

  const fetchTelefono = () => {
    fetch('/api/usuarios/telefono',{
      method: 'GET',
      credentials: 'include'
    })
      .then(res => res.json())
      .then(data => {
        if (data.telefono) {
          setTelefono(data.telefono);
        }
      })
      .catch(error => {
        console.log('Error al obtener el telefono del usuario:', error);
      });
  };


  const borrar = async (clienteId) =>{
    try{
      await fetch(`/api/clientes/eliminar/${clienteId}`,{
        method: 'PUT',
        credentials: 'include'
      })
      setClientes(prevClientes => prevClientes.filter(cliente => cliente.id !== clienteId));
    }catch(e){
      console.error(e);
    }
  };



  const boleta = async (nombre, nombrecliente, descripcion, telefonoC, codigoCli) =>{
    fetchTelefono();
    let telusuario = telefono;
    let data = 'COMPROBANTE \n'
              +`\n`
              + `Emisor: ${nombre} , tel: ${telusuario}\n`
              +`\n`
              + `Nombre del cliente: ${nombrecliente} \n`
              +`\n`
              + `Descripción: ${descripcion} \n`
              +`\n`
              + `Teléfono cliente: ${telefonoC}`
              +`\n`
              +`\n`
              + `Código: ${codigoCli}`;

    const doc = new jsPDF();


    doc.addImage(Dataimage, 'PNG',80, 10, 45, 45);
    doc.setFontSize(22);
    doc.text(data, 50, 70);
    doc.save('boleta.pdf');

  };

  const fetchClientes = () => {
    fetch('/api/clientes/lista',{
      method: 'GET',
      credentials: 'include'
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data.listaClientes)) {
          setClientes(data.listaClientes);
        } else {
          console.error('La respuesta no es un array:', data);
          setClientes([]);
        }
      })
      .catch(error => {
        console.log('Error al buscar clientes:', error);
        setClientes([]);
      });
  };

  useEffect(() => {
    fetchClientes();
    fetchNombre();
    fetchTelefono();
  }, []);
  
  

  const verCliente = () => {
    setMostrarFormulario(true);
  };

  const ocultarForm = () => {
    setMostrarFormulario(false);
  };

  const agregarCliente = (nuevoCliente) => {
    setClientes(prevClientes => [...prevClientes, nuevoCliente]); 
  };

  const agregarNombre = (nombre) => {
    setNombre(nombre);
  };

  const verFormCliente = () => {
    setMostrarFormularioCliente(true);
  };

  const ocultarFormCliente = () => {
    setMostrarFormularioCliente(false);
  };



  if (!nombre) {
    return (
      <div id="iddiv1">
        <FormularioInicio agregarNombre={agregarNombre} fetchClientes={fetchClientes} fetchTelefono={fetchTelefono}/>
      </div>
    );
  } else {
    return (
      <div className="paneladmin">
        <h1>Hola, {nombre}</h1>
        <button className="btn2" onClick={verCliente}>Añadir Cliente</button>
        {mostrarFormulario && <FormCliente agregarCliente={agregarCliente} ocultarForm={ocultarForm} fetchClientes={fetchClientes} />}
        

      {/* <Table className="text-white">
        <TableCaption>Lista de clientes.</TableCaption>
        <TableHeader>
          <TableRow>
            <TableHead className="w-[100px]">Nombre</TableHead>
            <TableHead>Descripcion</TableHead>
            <TableHead>Celular</TableHead>
            <TableHead >Estado</TableHead>
            <TableHead className="text-right">Opciones</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {clientes.map((cliente) => (
            <>
            <TableRow key={cliente.id}>
              <TableCell className="font-medium">{cliente.nombre}</TableCell>
              <TableCell>{cliente.descripcion}</TableCell>
              <TableCell>{cliente.telefono}</TableCell>
              <TableCell>{cliente.estado}</TableCell>
              <TableCell className="text-right"><button className="btnboleta" onClick={() => boleta(nombre, cliente.nombre, cliente.descripcion, cliente.telefono, cliente.codigo)}>Boleta</button>
                <button className="btn3" onClick={() => {setClienteSelec(cliente); verFormCliente();}}>Editar</button>
                <button className="btn3" onClick={() => borrar(cliente.id, setClientes)}>Eliminar</button></TableCell>
            </TableRow>
          </>
          ))}
        </TableBody>
      </Table> */}
        
        <ul className="ulcli">
          <p>Lista de clientes.</p>
            <li className="listacliente">
              <span>Nombre</span>
              <span>Descripción</span>
              <span>Teléfono</span>
              <span>Estado</span>
            </li>
          {clientes.map(cliente => (
            <li  key={cliente.id} className="listacliente">
              <span>{cliente.nombre}</span>
              <span className="descripcioncliente">{cliente.descripcion}</span>
              <span>{cliente.telefono}</span>
              <span>{cliente.estado}</span>
              <span className="listabotones">
                <button className="btnboleta" onClick={() => boleta(nombre, cliente.nombre, cliente.descripcion, cliente.telefono, cliente.codigo)}>Boleta</button>
                <button className="btn3" onClick={() => {setClienteSelec(cliente); verFormCliente();}}>Editar</button>
                <button className="btn3" onClick={() => borrar(cliente.id, setClientes)}>Eliminar</button>
              </span>
              
            </li>
          ))}

        </ul> 
        {clienteSelec && mostrarFormularioCliente &&
                <EditarCliente 
                  ocultarFormCliente={ocultarFormCliente} 
                  fetchClientes={fetchClientes}
                  id={clienteSelec.id} 
                  nombree={clienteSelec.nombre} 
                  descripcionn={clienteSelec.descripcion} 
                  cel={clienteSelec.telefono}
                />
            }
      </div>
    );
  }
}

export default ListaClientes;
