import React, { useState } from 'react';
import './ingreso.css';
import { Link, Navigate } from "react-router-dom";

function FormularioInicio () {
    const [nombre, setNombre] = useState('');
    const [contraseña, setContraseña] = useState('');
    const [mensaje, setMensaje] = useState('');
    const [redirigir, setRedirigir] = useState(false);

    const manejarEnvio = async (e) => {
        e.preventDefault();

        const verificarUsuario = {
            nombre: nombre,
            contraseña: contraseña
        };

        try{
            const respuesta = await fetch('/api/usuarios/iniciar-sesion', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(verificarUsuario)
            });

            const data = await respuesta.json();

            if (respuesta.ok){
                setMensaje(data.message);
                setNombre('');
                setContraseña('');
                setRedirigir(true);
            }else{
                setMensaje(data.message || 'Error al iniciar sesión');
            }
        }catch(error){
            console.error('Algo salió mal', error);
            setMensaje('Algo salió mal');
        }
    };

    return(
        <div className='center'>
            <h1>Iniciar sesión</h1>
            <form onSubmit={manejarEnvio}>
                <div className='inputbox'>
                    <span>Nombre:</span>
                    <input
                        type="text"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div className='inputbox'>
                    <span>Contraseña:</span>
                    <input
                        type='password'
                        value={contraseña}
                        onChange={(e) => setContraseña(e.target.value)}
                        required
                    />
                </div>
                <button id='btn10' className='inputbox' type="submit">Iniciar Sesión</button>
                <Link to="/Registrar">O registrate aqui.</Link>


            </form>
            {mensaje && <p>{mensaje}</p>}
        </div>
    );
};

export default FormularioInicio;
